<?phpnamespace App;use Illuminate\Database\Eloquent\Model;class Exam extends Model{	/**		TODO:		- Remove $timestamps	 */    // public $timestamps = false;        public function user()    {    	return $this->belongsTo('App\User');    }    public function category()    {    	return $this->belongsTo('App\Category');    }    public function user_exams()    {    	return $this->hasMany('App\User');    } }